import React, {useState} from "react";
import {Link
} from "react-router-dom";

export const AddChore = (props) => {
    const [chore, setChore] = useState({id: '', name: '', day: '', status: '', image: ''});
    const[message, setMessage] = useState('')

    function handleChore(e){
        const newChore = chore;
        newChore.name = e.target.value;
        setChore(newChore);
        console.log("New chore added", chore);
    }

    function handleDay(e){
        const newChore = chore;
        newChore.day = e.target.value;
        setChore(newChore);
        console.log("Date added", chore)
    }

    function handleStatus(e){
        const newChore = chore;
        newChore.status = e.target.value;
        setChore(newChore);
        console.log("Status added", chore)

    }

    function handleImage(e){
        const newChore = chore;
        newChore.image = e.target.value;
        setChore(newChore);
        console.log("Chore image added", chore)
    }

    function handleSubmit(e){
        e.preventDefault();
        props.addChore(chore);
        e.target.reset();
        setMessage(`You sucessfully added ${chore.image} ${chore.name} for ${chore.day} ${chore.status}`)
        setChore({id: '', name: '', day: '', status: '', image: ''})
    }

    return(
        
        message ? (
        <div class="add-chore-container">
        
            <Link to="/List">
            <button class='view-list'>View List</button>
            </Link>
            <button class="chore-btn" onClick={(setMessage(''))}> Add another Chore</button>
        </div>
    ) : (
        <form onSubmit={handleSubmit}>
            <input id="chore-name" class="input-chore" placeholder="chore" type="text" onchange={handleChore} required/>
            <br/>
            <input id="chore-day" class="input-day" placeholder="day" type="date"/>
            <br/>
            <button className="enroll-btn" type="submit">Submit</button>
            <button className="enroll-btn" type="reset" value="reset" >Cancel</button>
        </form>

    )
    )
}
